select.if:
SELECT vdsm_user_product_base.product_id, fn_calculateSearchWeight(click_count,is_hot,is_new,is_best,0,#{queryWeight[0]}, #{queryWeight[1]},#{queryWeight[2]},#{queryWeight[3]},#{queryWeight[4]}) weightValue FROM ( SELECT a.product_id FROM ( SELECT product_id FROM dsm_product WHERE dsm_product.product_status=1 AND FIND_IN_SET(cat_id,fn_getChildCatList(1)) UNION ALL SELECT product_id FROM dsm_product_base_attr WHERE STATUS=1 GROUP BY product_id
FIND_IN_SET(#{bean.valueId},GROUP_CONCAT(value_id))
) a GROUP BY a.product_id HAVING COUNT(*) > 1 ) p LEFT OUTER JOIN vdsm_user_product_base ON p.product_id=vdsm_user_product_base.product_id WHERE vdsm_user_product_base.product_status=1 ORDER BY WeightValue DESC LIMIT 0,#{totalNum}

SELECT a.product_id, a.cat_id, a.product_sn, a.product_name, a.click_count, a.brand_id, a.shop_id, a.shop_name, a.min_price, a.max_price, a.shop_price, a.warn_number, a.product_brief, a.main_img, a.product_grade, a.product_status, a.is_best, a.is_new, a.is_hot FROM vdsm_user_product_base a, ( SELECT vdsm_user_product_base.product_id, fn_calculateSearchWeight(click_count,is_hot,is_new,is_best,0,#{queryWeight[0]}, #{queryWeight[1]},#{queryWeight[2]},#{queryWeight[3]},#{queryWeight[4]}) weightValue FROM ( SELECT a.product_id FROM ( SELECT product_id FROM dsm_product WHERE dsm_product.product_status=1 AND FIND_IN_SET(cat_id,fn_getChildCatList(#{catId})) UNION ALL SELECT product_id FROM dsm_product_base_attr WHERE STATUS=1 GROUP BY product_id
FIND_IN_SET(#{bean.valueId},GROUP_CONCAT(value_id))
) a GROUP BY a.product_id HAVING COUNT(*) > 1 ) p LEFT OUTER JOIN vdsm_user_product_base ON p.product_id=vdsm_user_product_base.product_id WHERE vdsm_user_product_base.product_status=1 ORDER BY WeightValue DESC LIMIT #{start},#{offset} ) b WHERE a.product_id=b.product_id ORDER BY b.weightValue DESC;

SELECT p.product_id, cat_id, product_sn, product_name, click_count, brand_id, shop_id, shop_name, min_price, max_price, shop_price, warn_number,product_brief, main_img, product_grade, product_status, is_best, is_new, is_hot FROM ( SELECT product_id FROM dsm_product WHERE dsm_product.product_status=1 AND FIND_IN_SET(cat_id,fn_getChildCatList(#{catId}))
) p LEFT OUTER JOIN vdsm_user_product_base ON p.product_id=vdsm_user_product_base.product_id WHERE vdsm_user_product_base.product_status=1 ORDER BY
min_price
max_price DESC
LIMIT 0,#{totalNum}

SELECT p.product_id, cat_id, product_sn, product_name, click_count, brand_id, shop_id, shop_name, min_price, max_price, shop_price, warn_number,product_brief, main_img, product_grade, product_status, is_best, is_new, is_hot FROM ( SELECT a.product_id FROM ( SELECT product_id FROM dsm_product WHERE dsm_product.product_status=1 AND FIND_IN_SET(cat_id,fn_getChildCatList(#{catId})) UNION ALL
SELECT product_id FROM dsm_product_base_attr WHERE attr_id=#{bean.attrId} AND value_id=#{bean.valueId}
) a GROUP BY a.product_id HAVING COUNT(1) > #{beanSize} ) p LEFT OUTER JOIN vdsm_user_product_base ON p.product_id=vdsm_user_product_base.product_id WHERE vdsm_user_product_base.product_status=1 ORDER BY
min_price
max_price DESC
LIMIT 0,#{totalNum}
